// Main.jsx
import React, { useState } from 'react';
import styles from "./styles.module.css";
import Blog from "../Blog";
import Api from '../Api';

const Main = () => {
    const [showBlog, setShowBlog] = useState(false);
    const [showApi, setShowApi] = useState(false); // State for showing the Api component

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    const handleShowBlog = () => {
        setShowBlog(true);
        setShowApi(false); // Hide the Api component when showing the Blog component
    };

    const handleShowApi = () => {
        setShowApi(true);
        setShowBlog(false); // Hide the Blog component when showing the Api component
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <div className={styles.navbar_items}>
                    <h1>Daily Blog</h1>
                </div>
                <div className={styles.navbar_items}>
                    <button className={styles.white_btn} onClick={handleShowBlog}>
                        Blog List
                    </button>
                    <button className={styles.white_btn} onClick={handleLogout}>
                        Logout
                    </button>
                    {/* Button to show the Api component */}
                    <button className={styles.white_btn} onClick={handleShowApi}>
                        Show Api
                    </button>
                </div>
            </nav>

            {/* Render either Blog or Api based on state */}
            {showBlog && <Blog />}
            {showApi && <Api />}
        </div>
   );
};

export default Main;
