// Api.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Api = () => {
  const [users, setUsers] = useState([]);
  const [render, setRender] = useState(false); // assuming render is a state used for re-rendering

  useEffect(() => {
    const getAllUsers = async () => {
      try {
        const response = await axios.get("http://localhost:8007/users");
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    getAllUsers();
  }, [render]); // Re-run effect when 'render' state changes

  return (

    <table>
        <h2>Api Show all data </h2>
      <tbody>
        {users.map((user) => (
          <tr key={user._id}>
            <td>{user.title}</td>
            <td>{user.des}</td>
            <td>{user.date}</td>
            <td><img className="img img-fluid" src={`http://localhost:8007/${user.image}`} alt="User" /></td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Api;
