import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Blog() {
  const [users, setUsers] = useState([]);
  const [render, setRender] = useState(false);
  const [inputs, setInputs] = useState({
    title: "",
    des: "",
    active: false // Default to false
  });
  const [image, setImage] = useState();
  const [isUpdating, setIsUpdating] = useState(false);
  const [selectedUserId, setSelectedUserId] = useState(null);

  useEffect(() => {
    const getAllUsers = async () => {
      const getAllData = await axios.get("http://localhost:8007/users");
      setUsers(getAllData.data);
    };
    getAllUsers();
  }, [render]);

  useEffect(() => {
    if (selectedUserId) {
      const selectedUser = users.find(user => user._id === selectedUserId);
      if (selectedUser) {
        setInputs({
          title: selectedUser.title,
          des: selectedUser.des,
          active: selectedUser.active
        });
      }
    }
  }, [selectedUserId, users]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedUserId) {
        const formdata = new FormData();
        formdata.append("title", inputs.title);
        formdata.append("des", inputs.des);
        formdata.append("image", image);
        formdata.append("active", inputs.active);

        await axios.put(`http://localhost:8007/users/update/${selectedUserId}`, formdata);
        setRender(!render); // Toggle render to refresh user data
        setIsUpdating(false); // Close update form
        setSelectedUserId(null); // Reset selected user ID
      } else {
        // Handle form submission for new user creation
        const formdata = new FormData();
        formdata.append("title", inputs.title);
        formdata.append("des", inputs.des);
        formdata.append("image", image);
        formdata.append("active", inputs.active);

        await axios.post("http://localhost:8007/users", formdata);
        setRender(!render); // Toggle render to refresh user data
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleActiveChange = (e) => {
    setInputs({ ...inputs, active: e.target.checked });
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8007/users/${id}`);
      setRender(!render); // Refresh user data after deletion
    } catch (error) {
      console.log(error);
    }
  };

  const handleUpdate = (id) => {
    setSelectedUserId(id);
    setIsUpdating(true);
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6">
          <form onSubmit={handleSubmit} encType="multipart/form-data">
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
              <input type="text" value={inputs.title} onChange={(e) => setInputs({ ...inputs, [e.target.name]: e.target.value })} name="title" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">Description</label>
              <input type="text" value={inputs.des} onChange={(e) => setInputs({ ...inputs, [e.target.name]: e.target.value })} name='des' className="form-control" id="exampleInputPassword1" />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">Image</label>
              <input type="file" onChange={(e) => setImage(e.target.files[0])} name="image" className="form-control" id="exampleInputPassword1" />
            </div>
            <div className="mb-3 form-check">
              <input type="checkbox" className="form-check-input" id="activeCheckbox" checked={inputs.active} onChange={handleActiveChange} />
              <label className="form-check-label" htmlFor="activeCheckbox">Active</label>
            </div>
            <button type="submit" className="btn btn-primary">{isUpdating ? 'Update' : 'Submit'}</button>
          </form>
        </div>
        <div className="col-md-6">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">Title</th>
                <th scope="col">Description</th>
                <th scope="col">Date</th>
                <th scope="col">Image</th>
                <th scope="col">Actions</th>
              </tr>
            </thead>
            <tbody>
              {users && users.map((user) => (
                <tr key={user._id}>
                  <td>{user.title}</td>
                  <td>{user.des}</td>
                  <td>{user.date}</td>
                  <td><img className="img img-fluid" src={`http://localhost:8007/${user.image}`} alt="users" /></td>
                  <td>
                    <button className="btn btn-danger" onClick={() => handleDelete(user._id)}>Delete</button>
                    <button className="btn btn-warning mx-2" onClick={() => handleUpdate(user._id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Blog;
