import React from 'react';

const PostDetails = ({ post }) => {
  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <img src={`http://localhost:8007/${post.image}`} className="img-fluid" alt="Post" />
          <h2>{post.title}</h2>
          <p>{post.des}</p>
          <p>{post.date}</p>
        </div>
      </div>
    </div>
  );
};

export default PostDetails;
