import React,{useEffect,useState} from 'react'
import axios from 'axios'
function Blog() {
const[users,setUsers]=useState([]);
const[render,setRender]=useState(false);
const[inputs,setInputs]=useState({
  title: "",
  des: "",
});
const [image,setProfile]=useState();
useEffect(()=>{

  const getAllUsers=async ()=>{
    const getAllData=await axios.get("http://localhost:8007/users");
    setUsers(getAllData.data)
  };
  getAllUsers();
},[]);

// For Data Submited
const formdata=new FormData();
formdata.append("title",inputs.title);
formdata.append("des",inputs.des);
formdata.append("image",image);

const handleSubmit= async (e)=>{
  e.preventDefault();

try {
  await axios.post("http://localhost:8007/users",formdata);
  setRender(true);


} catch (error) {
  console.log(error);

}

}

  return (

<div className="container">
  <div className="row">
    <div className="col-md-45"  style={{backgroundColor:"blue"}}>
      <h1 className="text-center text-white">UPLOAD BLOG :-</h1>
    </div>

<div className="col-md-6">
<form onSubmit={handleSubmit}  encType="multipart/form-data">
  <div class="mb-3">
    <label for="exampleInputEmail1" class="form-label">Title</label>
    <input type="text" value={inputs.title} onChange={(e)=>setInputs({ ...inputs, [e.target.name]: e.target.value })} name="title"      class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
    </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Description</label>
    <input type="text" value={inputs.des} onChange={(e)=>setInputs({ ...inputs, [e.target.name]: e.target.value })}     name='des'  class="form-control" id="exampleInputPassword1"/>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Image</label>
    <input type="file"  onChange={(e)=>setProfile(e.target.files[0])}  name="image"   class="form-control" id="exampleInputPassword1"/>
  </div>

  <button type="submit" class="btn btn-primary">Submit</button>
</form>

</div>
<div className="col-md-6">
<table class="table">
  <thead>
    <tr>
      <th scope="col">Title</th>
      <th scope="col">Description</th>
      <th scope="col">Date</th>
      <th scope="col">Image</th>
    </tr>
  </thead>
  <tbody>
    {users && users.map((user)=>{

return(
  <tr>
      <td>{user.title}</td>
      <td>{user.des}</td>
      <td>{user.date}</td>
      <td><img className="img img-fluid" src={`http://localhost:8007/${user.image}`} alt="users"/></td>
    </tr>

)


    })}

  </tbody>
</table>


</div>

  </div>

</div>

  )
}

export default Blog;
