import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Api = () => {
  const [users, setUsers] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const getAllUsers = async () => {
      try {
        const response = await axios.get("http://localhost:8007/users");
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    getAllUsers();
  }, []);

  const handleReadMore = (post) => {
    setSelectedPost(post);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <>
      <div className="container">
        <h2 className="text-center mb-10 mt-[15] text-3xl font-bold underline hover:text-blue-500">We share what weâ€™ve learned</h2>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {users.map((user) => (
            <div key={user._id} className="col">
              <div className="card h-100">
                <img src={`http://localhost:8007/${user.image}`} className="card-img-top img-thumbnail" alt="User" />
                <div className="card-body">
                  <h5 className="card-text text-2xl text-center">{user.title}</h5>
                  <p className="card-text text-sm text-center">{user.des}</p>
                  <p className="card-text text-center text-lg"><small className="text-muted">{user.date}</small></p>
                  <button onClick={() => handleReadMore(user)} className='text-xl font-medium text-red-500 p-[5px] bg-blue-500 ml-[35%] rounded-md hover:bg-gray-500'>Read More</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {modalOpen && selectedPost && (
        <div className="fixed inset-0 z-50 flex items-center justify-center">
          <div className="absolute inset-0 bg-gray-900 opacity-50"></div>
          <div className="modal-container bg-gray-500 rounded-lg overflow-hidden">
            <span className="close absolute top-0 right-0 m-6 text-gray-400 cursor-pointer" onClick={closeModal}>&times;</span>
            <div className="p-8">
              <img src={`http://localhost:8007/${selectedPost.image}`} className="w-[70%] mb-4" alt="User" rounded-md        />
              <h2 className="text-3xl font-bold mb-2">{selectedPost.title}</h2>
              <p className="text-base text-gray-700 mb-4">{selectedPost.des}</p>
              <p className="text-sm text-gray-500">{selectedPost.date}</p>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Api;
