import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Api = () => {
  const [users, setUsers] = useState([]);
  const [render, setRender] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const getAllUsers = async () => {
      try {
        const response = await axios.get("http://localhost:8007/users");
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    getAllUsers();
  }, [render]);

  const handleReadMore = (post) => {
    setSelectedPost(post);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div className="container">
      <h2 className="text-center mb-10 mt-[15] text-3xl font-bold underline hover:text-blue-500">We share what we’ve learned</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-2">
        {users.map((user) => (
          <div key={user._id} className="flex justify-center">
            <div className="max-w-sm rounded overflow-hidden shadow-lg">
              <img src={`http://localhost:8007/${user.image}`} className="w-full" alt="User" />
              <div className="px-6 py-4">
                <div className="font-bold text-xl mb-2  text-center  ">{user.title}</div>
                <p className="text-gray-700 text-base text-center       ">{user.des}</p>
              </div>
              <div className="px-6 pt-4 pb-2">
                <button onClick={() => handleReadMore(user)} className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4  ml-[30%]             rounded-full'>Read More</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {modalOpen && selectedPost && (
        <div className="fixed inset-0 flex items-center justify-center z-50">
          <div className="absolute inset-0 bg-gray-900 opacity-50"></div>
          <div className="modal-container bg-white rounded-lg overflow-hidden">
            <span className="close absolute top-0 right-0 m-6 text-gray-400 cursor-pointer" onClick={closeModal}>&times;</span>
            <div className="p-8">
              <img src={`http://localhost:8007/${selectedPost.image}`} className="w-[50%] mb-4" alt="User" />
              <h2 className="text-3xl font-bold mb-2">{selectedPost.title}</h2>
              <p className="text-base text-gray-700 mb-4">{selectedPost.des}</p>
              <p className="text-sm text-gray-500">{selectedPost.date}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Api;
